package org.demo.portalteleport.event;

import net.minecraft.network.chat.Component;
import net.minecraft.server.level.ServerPlayer;
import net.minecraft.world.effect.MobEffectInstance;
import net.minecraft.world.effect.MobEffects;
import net.neoforged.bus.api.SubscribeEvent;
import net.neoforged.neoforge.event.entity.living.LivingHurtEvent;

/**
 * 弹跳加成：当玩家因摔落受到伤害时，给予 5 秒跳跃提升 I，并显示趣味提示。
 */
public class FallBounceHandler {
    @SubscribeEvent
    public void onLivingHurt(LivingHurtEvent event) {
        if (!(event.getEntity() instanceof ServerPlayer player)) return;
        var src = event.getSource();
        if (src.isFall()) {
            player.addEffect(new MobEffectInstance(MobEffects.JUMP, 5 * 20, 0, false, true));
            player.sendSystemMessage(Component.literal("弹跳的步伐！下次小心脚下~"));
        }
    }
}
